CREATE TABLE `ecom`.`users` (`id` INT NOT NULL AUTO_INCREMENT , `username` VARCHAR(50 ) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL , `email` VARCHAR(255) NOT NULL , `password` VARCHAR(255) NOT NULL , `role` VARCHAR(50) NOT NULL , `status` VARCHAR(25) NOT NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;